package ejercicios;

public class ejercicio6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x, y, z, resultado;
		x = 3;
		y = 4;
		z = 2;
		resultado = x+y*z;
		System.out.println(resultado);
		resultado = (x+y)*z;
		System.out.println(resultado);
		resultado = ++x%y;
		System.out.println(resultado);
		resultado = x+z/y;
		System.out.println(resultado);
		//ENUNCIADO
		/*A2.7. Operadores
Dadas las variables enteras x=3, y=4 y z=2 indica qué se asignará 
a la variable resultado en cada expresión:
resultado = x+y*z;
resultado = (x+y)*z;
resultado=++x%y;
resultado = x+z/y; */
		

	}

}
