package ejercicios;

public class ejercicio6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x, y, z, resultado;
		x = 3;
		y = 4;
		z = 2;
		resultado = x+y*z;
		System.out.println(resultado);
		resultado = (x+y)*z;
		System.out.println(resultado);
		resultado = ++x%y;
		System.out.println(resultado);
		resultado = x+z/y;
		System.out.println(resultado);
		

	}

}
